import React from "react";
import LanguageIcon from "@material-ui/icons/Language";
import GitHubIcon from "@material-ui/icons/GitHub";
import YouTubeIcon from "@material-ui/icons/YouTube";

function Card(props) {
  // For a given portfolio item a combination of links can be included, this
  // function ensures a corresponding icon is returned, with a catch all "Link"
  function getIcon(linkName) {
    if (linkName === "site") {
      return <LanguageIcon class="card-link-icon" />;
    } else if (linkName === "demo") {
      return <YouTubeIcon class="card-link-icon" />;
    } else if (linkName === "source") {
      return <GitHubIcon class="card-link-icon" />;
    } else {
      return "Link";
    }
  }

  function onHover() {
    console.log("hover");
  }

  return (
    <div class="card-item">
      <h3>{props.item.title}</h3>
      <p>{props.item.content}</p>
      <div class="card-links">
        {Object.keys(props.item.links).map((key, index) => {
          const link = props.item.links[key];
          const linkName = key;

          return (
            <span class="card-link">
              <a href={link} onHover={onHover()}>{getIcon(linkName)}</a>{" "}
            </span>
          );
        })}
      </div>
    </div>
  );
}
export default Card;
